}

export enum LayoutMeasuringStrategy {
  Always,
  BeforeDragging,
  WhileDragging,
}

export enum LayoutMeasuringFrequency {
  Optimized = 'optimized',
}

export interface LayoutMeasuring {
  strategy: LayoutMeasuringStrategy;
  frequency: LayoutMeasuringFrequency | number;
